	# 		  aes(y = .epred,
# 		  	group = interaction(condition, .draw)),
# 		  linetype = "solid",
# 		  alpha = 0.1,
# 		  linewidth = 3/4) +
stat_summary(data = epreds,
			 aes(y = .value),
			 fun.data = \(x) mean_qi(x, .width = 0.95),
			 geom = "ribbon",
			 alpha = 0.5,
			 linewidth = 0) +
	stat_summary(data = epreds,
				 aes(y = .value,
				 	linetype = condition),
				 fun = "mean",
				 geom = "line",
				 colour = "black",
				 linewidth = 3/4) +
	geom_hline(yintercept = 1/2, 
