	stat_summary(fun = mean,
				 geom = "point",
				 colour = "black",
				 size = 2.5,
				 stroke = 3/4) +
	labs(x = "Time (ms)",
		 y = "P(Target looking)",
		 colour = "Condition",
		 fill = "Condition",
		 linetype = "Condition",
		 shape = "Condition") +
	theme(legend.title = element_blank(),
		  axis.title.x = element_blank()) +

