	plot_layout(ncol = 1) &
	plot_annotation(tag_levels = "A") +
	scale_linetype_manual(values = rev(c("solid", "dashed"))) &
	scale_shape_manual(values = c(1, 2)) &
	scale_x_continuous(labels = \(x) format((x * 1e2)+300, 
											big.mark = ",")) &
