data_time_related_vnone |> 
	summarise(.prop = mean(.prop),
			  .by = c(id, timebin, lp, condition, age)) |> 
	ggplot(aes(timebin, .prop, 
			   colour = condition,
			   fill = condition,
			   shape = condition,
			   linetype = condition)) +
	facet_wrap(~lp) +
	# geom_rect(data = diff_rect,
	# 		  aes(xmin = xmin,
	# 		  	xmax = xmax,
